UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Ihr wendet zwei cenarische Pflanzensalben auf die Windblüte an. Sofort beginnt sie sich zu verwandeln. Sie blüht auf und wird zu einer vor Leben sprühenden und gesunden Pflanze. ', RequestItemsText_loc2='', OfferRewardText_loc2='Vous appliquez deux doses de baume cénarien sur la fleur-de-vent. Elle commence immédiatement à se défaire de sa forme souillée pour s''épanouir en une plante vibrante de santé. ' WHERE entry=4115;
