UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Nehmt den Plunder, Dummkopf. Und verlasst mich, damit ich mich meinen Studien widmen kann!', RequestItemsText_loc2='', OfferRewardText_loc2='Prenez votre babiole, imbécile. Et laissez-moi poursuivre mes recherches !' WHERE entry=4463;
UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Ihr wendet ein wenig cenarische Pflanzensalbe auf die Liedblume an. Sofort beginnt sie sich zu verwandeln. Sie blüht auf und wird zu einer vor Leben sprühenden und gesunden Pflanze. ', RequestItemsText_loc2='', OfferRewardText_loc2='Vous appliquez du baume végétal de Cénarius sur la fleur-de-chant. Elle commence immédiatement à abandonner sa forme corrompue pour fleurir comme une plante vivace et radieuse. ' WHERE entry=4464;
