UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Nochmals danke, $N. Viel Spaß mit diesen Losen, und erzählt Euren Freunden vom Dunkelmond-Jahrmarkt!', RequestItemsText_loc2='', OfferRewardText_loc2='Encore merci, $N. Profitez bien de vos bons, et parlez à vos amis de la foire de Sombrelune !' WHERE entry=7939;
