UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Hier, $N. Nehmt diese Lose des Dunkelmond-Jahrmarkts und vergnügt Euch damit. Gebt sie nicht alle auf einmal aus, wie man so schön sagt!', RequestItemsText_loc2='', OfferRewardText_loc2='Et voilà, $N. Des bons de la foire de Sombrelune rien que pour vous. Comme on dit, ne faites pas d’excès !' WHERE entry=7942;
