UPDATE locales_quest SET RequestItemsText_loc3='Habt Ihr Neuigkeiten, $N?', OfferRewardText_loc3='Ihr habt Euren Beitrag geleistet, $N! Die Allianz wird den barbarischen Ausschreitungen der Horde nicht tatenlos gegenüber stehen!', RequestItemsText_loc2='Quelles nouvelles m''apportez-vous, $N ?', OfferRewardText_loc2='Vous avez fait votre part, $N. L’Alliance ne peut pas laisser passer les actes de barbarie de la Horde sans réagir !' WHERE entry=8375;
UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Wieder einmal ehrt Ihr die Allianz, $N! Wenn wir so weiter machen, wird es nicht mehr lange dauern, bis die Horde vernichtet ist. ', RequestItemsText_loc2='', OfferRewardText_loc2='Vous continuez à faire honneur à l’Alliance, $N.  La Horde sera écrasée dans peu de temps si nous maintenons nos efforts. ' WHERE entry=8383;
