UPDATE locales_quest SET RequestItemsText_loc3='', OfferRewardText_loc3='Ihr werdet Eurem Ruf gerecht, $N. Ohne Kämpfer wie Euch, würde die Allianz keine Chance gegen die barbarischen Streitkräfte der Horde haben. ', RequestItemsText_loc2='', OfferRewardText_loc2='Vous êtes à la hauteur de votre réputation, $N.  Sans des gens comme vous, l''Alliance n''aurait pas une chance contre les forces sauvages de la Horde. ' WHERE entry=8385;
